@implementation SYNUAPI

#pragma mark - URls
NSString *const kHost=@"http://210.30.208.126/";
//NSString *const kHost=@"http://210.30.208.126/";
NSString *const kCheckCode=@"(%@)/CheckCode.aspx";//获取验证码
NSString *const kLogIn=@"(%@)/default2.aspx";//登录教务处的地址
NSString *const kManagementSystemMainPage=@"(%@)/xs_main.aspx?xh=%@";//教务管理系统的首页
NSString *const kStudentAvatar=@"(%@)/readimagexs.aspx?xh=%@";//学生头像
NSString *const kStudentInformation= @"(%@)/xsgrxx.aspx?xh=%@&xm=%@&gnmkdm=N121501";//学生个人信息页面
NSString *const kStudentCourses=@"(%@)/xscjcx.aspx?xh=%@&xm=%@&gnmkdm=N121605";//学生成绩列表 列表有多种类型,根据post参数不同。。

#pragma mark - Public
+(NSString*)generateMainPageUrl{
    return kHost;
}

+(NSString*)generateCheckCodeUrl:(NSString*)sessionID{
    return [NSString stringWithFormat:[kHost stringByAppendingPathComponent:kCheckCode],sessionID];
}

+(NSString*)generateLogInUrl:(NSString*)sessionID{
    return [NSString stringWithFormat:[kHost stringByAppendingPathComponent:kLogIn],sessionID];
}

+(NSString*)generateLogInBody:(NSString*)userName pwd:(NSString*)pwd checkCode:(NSString*)checkCode {
    return [NSString stringWithFormat:kLogInBody,userName,pwd,checkCode];
}

+(NSString*)generateStudentInformationUrl:(NSString*)sessionID studentID:(NSString*)studentID studentName:(NSString*)studentName{
    return [NSString stringWithFormat:[kHost stringByAppendingPathComponent:kStudentInformation],sessionID,studentID,studentName];
}

+(NSString*)generateStudentAvantarUrl:(NSString*)sessionID studentID:(NSString*)studentID {
    return [NSString stringWithFormat:[kHost stringByAppendingPathComponent:kStudentAvatar],sessionID,studentID];
}

+(NSString*)generateStudentCoursesUrl:(NSString*)sessionID studentID:(NSString*)studentID studentName:(NSString*)studentName{
    return [NSString stringWithFormat:[kHost stringByAppendingPathComponent:kStudentCourses],sessionID,studentID,studentName];
}
@end
